teian:common-panel.script:
    $("#teian-search-string-label").text($($x.xpath($("#teian-search-string-label").attr('ref'))[0]).text());
    $("#teian-search-button").text($($x.xpath("simpath:instance('ui-lang')//teian:common-panel[@id = 'selected-wrap-server-annotator-panel']/teian:annotator-panel-element[@id = 'teian-search-button']")[0]).text()).click(function() {teian.ui.fGenerateSearchList();});
    $("#teian-search-list-label").text($($x.xpath($("#teian-search-list-label").attr('ref'))[0]).text());
    teian.ui.fGenerateSearchList = function() {
    	var oSelectElement = $("#teian-search-list");
	$x.submission({
		"ref" : "simpath:instance('search-results')",
		"resource" : $("#" + teian.ui.activeAnnotatorId)[0].sServiceURI + $("#teian-search-criterion-input").val(),
		"mode" : "synchronous",
		"method" : "get"
	});
	oSelectElement.html($("*", $($x.xpath("simpath:instance('search-results')/*")[0])));
	//oSelectElement[0].selectedIndex = -1;
    };
    $("#teian-search-criterion-input").keyup(function(event){
    	if(event.keyCode == 13){
		$("#teian-search-button").click();
    	}
    });
teian.ui['selected-wrap-server-annotator'] = $("#teian-selected-wrap-server-annotator-dialog").dialog({
	autoOpen: false,
	width : 700,
	height : 300,
	modal : true,
	buttons: {
		"Save": function() {
			$( this ).dialog( "close" );
			var oActiveAnnotator = $("#" + teian.ui.activeAnnotatorId)
			, oActiveAnnotator0 = oActiveAnnotator[0]
			;
			$(oActiveAnnotator0['oAnnotatorMarkup']).attr(oActiveAnnotator0.sMainAttrName, $("#teian-search-list").val());
			teian.annotator[0](oActiveAnnotator0, 'selected-wrap-server');
			$(oActiveAnnotator0['oAnnotatorMarkup']).attr(oActiveAnnotator0.sMainAttrName, '');
			//reset the UI elements
			$("#teian-search-criterion-input").val('');
			$("#teian-search-list")[0].options.length = 0;
		},
		"Cancel": function() {
			$( this ).dialog( "close" );
			teian.utils.oSavedSelection = null;
			//reset the UI elements
			$("#teian-search-criterion-input").val('');
			$("#teian-search-list")[0].options.length = 0;
		}
	}
});

teian:annotator-panel.script:
$("#date-dialog").attr('title', $x.xpath("simpath:instance('ui-lang')//teian:annotator[@id = 'date-annotator']/teian:annotator-panel/teian:annotator-panel-title/text()"));
$("label[for='teian-date-when']").text($($x.xpath($("label[for='teian-date-when']").attr('ref'))[0]).text());
teian.ui['date-annotator'] = $("#date-dialog").dialog({
	autoOpen: false,
	width : 700,
	height : 200,
	modal : true,
	buttons: {
		"Save": function() {
			$( this ).dialog( "close" );
			$($("#date-annotator")[0]['oAnnotatorMarkup']).attr('when', $("#teian-date-when").val());
			teian.annotator[0]($("#date-annotator")[0], 'selected-wrap-parameterized');
			$($("#date-annotator")[0]['oAnnotatorMarkup']).attr('when', '');
			$("#teian-date-when").val('');
		},
		"Cancel": function() {
			$( this ).dialog( "close" );
			teian.utils.oSavedSelection = null;
			$("#teian-date-when").val('');
		}
	}
});

