$("#help-dialog").attr('title', $x.xpath("simpath:instance('ui-lang')//teian:annotator[@id = 'help-button']/teian:annotator-panel/teian:annotator-panel-title/text()"));
//alert($x.xpath("simpath:instance('ui-lang')//teian:annotator[@id = 'help-button']/teian:annotator-panel/teian:annotator-panel-element/@xlink:href")[0].value);
$("#help-dialog-content").load($x.xpath("simpath:instance('ui-lang')//teian:annotator[@id = 'help-button']/teian:annotator-panel/teian:annotator-panel-element/@xlink:href")[0].value + ' .article, .copyright');
