	autoOpen: false,
	width : 1000,
	height : 500,
	modal : true,
	buttons: {
		"Close": function() {
			$( this ).dialog( "close" );
		}
	}
});

$("#clear-entity-dialog").attr('title', $x.xpath("simpath:instance('ui-lang')//teian:annotator[@id = 'clear-entity-button']/teian:annotator-panel/teian:annotator-panel-title/text()"));
$("#clear-entity-label").text($x.xpath($("#clear-entity-label").attr('ref')));
$("#help-dialog-content").load($x.xpath("simpath:instance('ui-lang')//teian:annotator[@id = 'help-button']/teian:annotator-panel/teian:annotator-panel-element/@xlink:href")[0].value + ' .article, .copyright');
teian.ui['clear-entity-dialog'] = $("#clear-entity-dialog").dialog({
	autoOpen: false,
	width : 390,
	height : 200,
	modal : true,
	buttons: {			
		"Clear": function() {
			$(this).dialog("close");
			$(teian.utils.oEntityToClear).replaceWith($(teian.utils.oEntityToClear).text());
			$("#clear-entity-label").text($x.xpath($("#clear-entity-label").attr('ref')));
		},			
		"Cancel": function() {
			$(this).dialog("close");
			teian.utils.oEntityToClear = null;
			$("#clear-entity-label").text($x.xpath($("#clear-entity-label").attr('ref')));
		}				
	},
	open: function(event, ui) {
		$("#clear-entity-label").text($("#clear-entity-label").text().replace(/%entityName/, teian.utils.oEntityToClear.nodeName));
	}
});
